package RepetitiveStatement;

import java.util.Scanner;

public class Java_2739 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int x = sc.nextInt();
		for(int i = 1 ; i < 10 ; i++)
			System.out.println(x + " * " + i + " = " + i*x);
	}

}
